/* eslint-disable prettier/prettier */
import jq from "jquery";
// 获取最新配置
export function oauth2Client(url) {
  var oauth2Client = {
    begin: function(opts) {
      opts = jq.extend({
        clientId: false,
        authorizeUrl: false,
        redirectUri: window.location.href,
        responseType: 'code'
      }, opts);
      window.location.href = opts.authorizeUrl + '?client_id=' +
        opts.clientId + '&response_type=' + opts.responseType +
        '&redirect_uri=' + opts.redirectUri;
    },
    user: function(url) {
      jq.ajax({
        type: "get",
        url: url,
        async: true,
        data: {},
        success: function(data) {
          if (!data) {
            this.oAuthLoginPage();
            return null;
          }
          return data;
        },
        error: function(XMLHttpRequest, textStatus, errorThrown) {
          alert("当前登录用户为空，请检查当前地址是否正确");
          return null;
        },
      });
    },
    end: function(opts, fn, errFn) {
      opts = jq.extend({
        code: false,
        clientId: false,
        clientSecret: false,
        accessTokenUrl: false,
        redirectUri: window.location.href,
        userInfoUrl: false
      }, opts);
      if (!opts.code)
        return;

      jq.ajax({
        async: false,
        url: opts.accessTokenUrl,
        type: "post",
        //					dataType:'jsonp',
        //					jsonpCallback:'callback',
        data: {
          grant_type: "authorization_code",
          client_id: opts.clientId,
          client_secret: opts.clientSecret,
          code: opts.code,
          redirect_uri: opts.redirectUri
        },
        success: function(data) {
          if (data) {
            if (typeof data === 'string') {
              data = jq.parseJSON(data);
            }
            var accessToken = data.access_token;
            var expiresIn = data.expires_in;
          }
          fn(accessToken, expiresIn);
        },
        error: function(XMLHttpRequest, textStatus, errorThrown) {
          errFn(XMLHttpRequest, textStatus, errorThrown);
        },
        complete: function(XMLHttpRequest, textStatus) {}
      });

    }

  };

  return oauth2Client;
}
