/* eslint-disable prettier/prettier */
import jq from "jquery";
// 获取最新配置
export function oauth2Client(app, url) {
  var oauth2Client = {
    ouc: {
      user: null,
      oauth2User: url,
      clientId: "client",
      clientSecret: "secret"
    },
    getNoHashUrl: function() {
      return window.location.href.split("#")[0];
    },
    begin: function(opts) {
      opts = jq.extend({
        clientId: false,
        authorizeUrl: false,
        redirectUri: window.location.href,
        responseType: 'code'
      }, opts);
      window.location.href = opts.authorizeUrl + '?client_id=' +
        opts.clientId + '&response_type=' + opts.responseType +
        '&redirect_uri=' + opts.redirectUri;
    },
    user: function(url) {
      jq.ajax({
        type: "get",
        url: url,
        async: false,
        data: {},
        success: function(data) {
          debugger
          data = { "authorities": [{ "authority": "work" }], "details": { "remoteAddress": "192.168.7.22", "sessionId": "6776A9260CC06549C6744762F4090176" }, "authenticated": true, "principal": { "userId": 766372114, "username": "worker1", "password": "35dfe5f0ee3303ea7bd2d43e0ad30112d9c38f178d8e750f099c243b", "showname": "worker1", "officeCode": null, "roles": ["work"], "authorities": [{ "authority": "work" }], "accountNonExpired": true, "accountNonLocked": true, "credentialsNonExpired": true, "enabled": true }, "credentials": null, "name": "worker1" }

          if (!data) {
            oauth2Client.oAuthLoginPage();
            return;
          }

          app.config.globalProperties.$user = data;

        },
        error: function() {
          alert("当前登录用户为空，请检查当前地址是否正确");
          return;
        },
      });
    },
    oAuthLoginPage: function() {
      oauth2Client.begin({
        authorizeUrl: oauth2Client.ouc.oauth2User + "/authorize",
        clientId: oauth2Client.ouc.clientId,
        redirectUri: oauth2Client.getNoHashUrl()
      });
    }

  };

  return oauth2Client;
}
