import axios from "../common/axios";
import * as api from "../api";
import defaultValue from "./default";


export function configList (params) {
  return new Promise((resolve, reject) => {
    axios.get("http://192.168.5.34:33910/kd-config-server/develop-v1.1.x/kts-web-dev.json", {}).then(response => {
      resolve(response.data);
    }, err => {
      resolve(defaultValue.menuList);
    })
    .catch((error) => {
      resolve(defaultValue.menuList)
    })
  })
}

export function login (params) {
  return new Promise((resolve, reject) => {
  	let _url = api.LOGIN+'?username='+params.username+'&password='+params.password;
    axios.post(_url, {}).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}
export function msgList (params) {
  return new Promise((resolve, reject) => {
    axios.post(api.MSG_TOP_TEN, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(defaultValue.msgList);
    })
      .catch((error) => {
        resolve(defaultValue.msgList)
      })
  })
}


export function menuList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_MENU_TREE, {}).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}

export function resourceList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_RESOURCE_LIST, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(defaultValue.resource);
    })
      .catch((error) => {
        resolve(defaultValue.resource)
      })
  })
}

export function permissionList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_PERMISSION_LIST, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}

export function roleList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_ROLE_PAGE, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}

export function groupList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_GROUP_TREELIST, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}

export function userList (params) {
  return new Promise((resolve, reject) => {
    axios.get(api.SYS_USER_PAGE, { params }).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
      .catch((error) => {
        resolve(error)
      })
  })
}

export function modifyPwd (params) {
  return new Promise((resolve, reject) => {
  	let _url = api.CHANGE_PWD+'?userId='+params.userId+'&oldPassword='+params.oldPassword+'&newPassword='+params.newPassword;
    axios.post(_url, {}).then(response => {
      resolve(response.data);
    }, err => {
      resolve(err);
    })
    .catch((error) => {
      resolve(error)
    })
  })
}
