/* eslint-disable prettier/prettier */
// 通用方法
export function formatTags(tags) {
  var newtags = {};
  tags.forEach(d => {
    var k = d.k || d.key || d.name;
    var v = d.v || d.val || d.value || "";
    newtags[k] = v;
  })
  return newtags;
}
/*** 
 *用户权限：
 *0：无权限
 *1：作业员
 *2：质检员
 *3：所有权限
 */
